let videos = [];
let currentVideo = 0;
let index = 0;
let hue = 0;
let pi = "3.";
let blobs = [];

function preload() {
  for (let i = 0; i < 10; i++) {
    videos.push(createVideo(["videos/" + i + ".webm", "videos/" + i + ".mov"]));
    videos[i].hide();
  }
  videos.push(createVideo(["videos/p.webm", "videos/p.mov"]));
  videos[10].hide();
}

function setup() {
  createCanvas(windowWidth, windowHeight);
  colorMode(HSB, 100);
  videos[3].play();
  blobs.push(new Blob("3"));
  currentVideo = 3;
  setInterval(function(){ nextDigit(); }, 600);
}

function draw() {
  background(240);

  for (let i = 0; i < blobs.length; i++) {
    blobs[i].update();
    blobs[i].show();
  }

  if (blobs.length > 50) {
    for (let i = blobs.length - 50; i > 0; i--) {
      blobs[i].alpha -= 0.4;
      if (blobs[i].alpha < 1) {
        blobs.splice(i, 1)
      }
    }
  }
  let w = windowWidth;
  if (windowWidth/windowHeight > 14/9) {
    w = windowWidth * 0.8;
  }
  let h = w / 16 * 9;
  image(videos[currentVideo], (windowWidth - w) * 0.5, windowHeight - h, w, h);
  textSize(windowWidth/15);
  fill(80);
  textAlign(CENTER);
  stroke(50);
  strokeWeight(3);
  text("The Random Digits of Pi", windowWidth/2, windowWidth/14)
  textSize(windowWidth/35);
  text("with Daniel Shiffman", windowWidth/4*3, windowWidth/9)
}

function nextDigit() {
  index += 1;
  let newDigit = pi.substring(index, index + 1);
  blobs.push(new Blob(newDigit));
  if (newDigit == ".") {
    newDigit = 10;
  }
  videos[currentVideo].stop();
  currentVideo = parseInt(newDigit);
  videos[currentVideo].play();
}

class Blob {
  constructor(digit) {
    this.digit = digit;
    this.x = random(windowWidth);
    this.y = random(windowHeight);
    this.d = random(40,100);
    this.hue = random(100);
    this.vx = 0;
    this.vy = 0;
    this.alpha = 80;
  }
  show() {
    noStroke();
    fill(this.hue, 70, 70, this.alpha);
    ellipse(this.x, this.y, this.d);
    fill(100, this.alpha);
    textAlign(CENTER);
    textSize(this.d * 0.7);
    text(this.digit, this.x, this.y + this.d / 4);
  }
  update() {
    this.x += this.vx + random(-2, 2);
    this.y += this.vy + random(-2, 2);
  }
}
